<?php

namespace unit\build\Design\Frame;

use skewer\build\Design\Frame;

/**
 * Generated by PHPUnit_SkeletonGenerator on 2013-03-25 at 17:59:23.
 */
class FrameDesignApiTest extends \Codeception\Test\Unit
{
    /**
     * @covers \skewer\build\Design\Frame\Api::getModuleTitleByName
     */
    public function testGetModuleTitleByName()
    {
        // по хорошему надо добавить фиктивный модуль и проверить его

        $this->assertSame(
            \Yii::t('adm', 'main_module_on_page'),
            Frame\Api::getModuleTitleByName('content')
        );

        $this->assertSame(
            'Новости',
            Frame\Api::getModuleTitleByName('News')
        );

        $this->assertSame(
            'Страница сайта',
            Frame\Api::getModuleTitleByName('Main')
        );

        $this->assertSame(
            'kKJbvka',
            Frame\Api::getModuleTitleByName('kKJbvka')
        );
    }
}
